import React, { useState } from "react";
import Base from "../core/Base";
import { isAuthenticated } from "../auth/helper";
import { Link } from "react-router-dom";
import { createCategory } from "./helper/adminapicall";

const AddCategory = () => {
  const [name, setName] = useState("");
  const [error, setError] = useState(false);
  const [success, setSuccess] = useState(false);

  const { user, token } = isAuthenticated();

  const goBack = () => {
    return (
      <div className="mt-5">
        <Link className="btn btn-sm btn-success mb-3" to="/admin/dashboard">
          Admin Home
        </Link>
      </div>
    );
  };

  const handleChange = (event) => {
    setName(event.target.value);
  };

  const onSubmit = (event) => {
    event.preventDefault();
    setError("");
    setSuccess(false);

    // backend  request fire
    createCategory(user._id, token, { name })
      .then((data) => {
        if (data.error) {
          setError(data.error);
        } else {
          setError("");
          setSuccess(true);
          setName("");
        }
      })
      .catch((err) => console.log(err));
  };

  const myCategoryForm = () => {
    return (
      <form>
        <div className="form-group">
          <p className="lead mt-3">Enter the category</p>
          <input
            type="text"
            className="form-control my-3"
            onChange={handleChange}
            value={name}
            autoFocus
            required
            placeholder="Eg. Summer"
          />
          <button onClick={onSubmit()} className="btn btn-outline-info">
            Create Category
          </button>
        </div>
      </form>
    );
  };

  const successMessage = () => {
    if (success) {
      return <h4 className="text-success">Category created successfully</h4>;
    }
  };

  const failureMessage = () => {
      return (
        error && (
          <h4 className="text-danger">Failed to create category - {error}</h4>
        )
      );
  };

  return (
    <Base
      title="Create a Category"
      description="Add new category for the store"
      className="container bg-info p-4"
    >
      <div className="row bg-white rounded">
        <div className="col-md-8 offset-md-2">
          {successMessage()}
          {failureMessage()}
          {myCategoryForm()}
          {goBack()}
        </div>
      </div>
      {/* <p className="text-white bg-danger text-center">{name}, {error}, {success}</p> */}
    </Base>
  );
};

export default AddCategory;
