import { makeStripePayment } from "./helper/coreapicalls";
import { createOrder } from "./helper/orderHelper";
import { emptyCart } from "./helper/cartHelper";

const StripeCheckout = ({
  products,
  setReload = (f) => f,
  reload = undefined,
}) => {
  const [data, setData] = useState({
    loading: false,
    success: false,
    error: "",
    address: "",
