import React, {useState} from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";

const AddProduct = () => {

    const [values, setValues] = useState({
        photo: "",
        name: "",
        description: "",
        price: "",
        category: "",
        stock: 0.,
        error: "",
        success: false,
      });
      const { photo, name, description, price, category, stock, error, success } = values;

      const handleChange = (data) => (event) => {
        setValues({ ...values, error: false, [data]: event.target.value });
      };

      const onSubmit = (event) => {
        event.preventDefault();
        // setValues({ ...values, error: false });
        // signup({ name, email, password })
        //   .then((data) => {
        //     if (data.error) {
        //       setValues({ ...values, error: data.error, success: false });
        //     } else {
        //       setValues({
        //         ...values
        //       });
        //     }
        //   })
        //   .catch(console.log("Error is SignUp - onSubmit()"));
      };

  const createProductForm = () => (
    <form>
      <span>Post photo</span>
      <div className="form-group">
        <label className="btn btn-block btn-success">
          <input
            onChange={handleChange("photo")}
            type="file"
            name="photo"
            accept="image"
            placeholder="choose a file"
          />
        </label>
      </div>
      <div className="form-group">
        <input
          onChange={handleChange("name")}
          name="photo"
          className="form-control"
          placeholder="Name"
          value={name}
        />
      </div>
      <div className="form-group">
        <textarea
          onChange={handleChange("description")}
          name="photo"
          className="form-control"
          placeholder="Description"
          value={description}
        />
      </div>
      <div className="form-group">
        <input
          onChange={handleChange("price")}
          type="number"
          className="form-control"
          placeholder="Price"
          value={price}
        />
      </div>
      <div className="form-group">
        <select
          onChange={handleChange("category")}
          className="form-control"
          placeholder="Category"
        >
          <option>Select</option>
          <option value="a">a</option>
          <option value="b">b</option>
        </select>
      </div>
      <div className="form-group">
        <input
          onChange={handleChange("quantity")}
          type="number"
          className="form-control"
          placeholder="Quantity"
          value={stock}
        />
      </div>

      <button
        type="submit"
        onClick={onSubmit}
        className="btn btn-outline-success"
      >
        Create Product
      </button>
    </form>
  );

  return (
    <Base
      title="Add Product here!"
      description="Welcome to product creation area"
      className="conatiner bg-info p-4"
    >
      <Link className="btn btn-sm btn-dark mb-3" to="/admin/dashboard">
        Admin Home
      </Link>
      <div className="row bg-dark text-white rounded">
        <div className="col-md-8 offset-md-2">
          {createProductForm()}
        </div>
      </div>
    </Base>
  );
};

export default AddProduct;
