import React, { useState, useEffect } from "react";
import Base from "../core/Base";
import { isAuthenticated } from "../auth/helper";
import { Link } from "react-router-dom";
import { createCategory } from "./helper/adminapicall";

const AddCategory = () => {

  const { user, authToken } = isAuthenticated();

  const [values, setValues] = useState({
    name: "",
    error: "",
    loading: false,
    createdCategory: "",
    getRedirect: false
  })

  const {name, error, loading, createdCategory, getRedirect} = values;

  const goBack = () => {
    return (
      <div className="mt-5">
        <Link className="btn btn-sm btn-success mb-3" to="/admin/dashboard">
          Admin Home
        </Link>
      </div>
    );
  };

  useEffect(() => {
    if (getRedirect) {
      const time = setTimeout(() => setValues({ getRedirect: 1 }), 3000);
      return () => {
        clearTimeout(time);
      };
    }
  }, [getRedirect]);

  const handleChange = (data) => (event) => {
    setValues({ ...values, error: false, loading: false, [data]: event.target.value });
  };

  const onSubmit = (event) => {
    event.preventDefault();
    setValues({...values, error: "", loading: true});

    // backend  request fire
    createCategory(user._id, authToken, { name }).then((data) => {
      if (data.error) {
        setValues({ ...values, error: data.error, loading: false });
      } else {
        setValues({
          error: "",
          name: "",
          createdCategory: data.name,
          getRedirect: true
        })
      }
    });
  };

  const createCategoryForm = () => {
    return (
      <form>
        <div className="form-group">
          <p className="lead mt-3">Enter the category</p>
          <input
            type="text"
            className="form-control my-3"
            onChange={handleChange}
            value={name}
            autoFocus
            required
            placeholder="Eg. Summer"
          />
          <button onClick={onSubmit} className="btn btn-outline-info">
            Create Category
          </button>
        </div>
      </form>
    );
  };

  const successMessage = () => {
    if (success) {
      return (
        <div className="row">
          <div className="col-md-8 offset-sm-2 text-left">
            <div
              className="alert alert-success"
              style={{ display: success ? "" : "none" }}
            >
              Category created successfully
            </div>
          </div>
        </div>
      );
    }
  };

  const failureMessage = () => {
    if (error) {
      return (
        <div className="row">
          <div className="col-md-8 offset-sm-2 text-left">
            <div
              className="alert alert-danger"
              style={{ display: error ? "" : "none" }}
            >
              {error}
            </div>
          </div>
        </div>
      );
    }
  };

  return (
    <Base
      title="Create a Category"
      description="Add new category for the store"
      className="container bg-info p-4"
    >
      <Link className="btn btn-sm btn-dark mb-3" to="/admin/dashboard">
          Admin Home
        </Link>
      <div className="row bg-white rounded">
        <div className="col-md-8 offset-md-2">
          {successMessage()}
          {failureMessage()}
          {createCategoryForm()}
        </div>
      </div>
      {/* <p className="text-white bg-danger text-center">{name}, {error}, {success}</p> */}
    </Base>
  );
};

export default AddCategory;
